const anchors=document.querySelectorAll('a[href*="#"]');for(let t of anchors)t.addEventListener("click",(function(e){e.preventDefault();const c=t.getAttribute("href").substr(1);document.getElementById(c).scrollIntoView({behavior:"smooth",block:"start"})}));const btn=document.querySelector(".burger-menu");addEventListener("click",t=>{let e=document.querySelector(".burgerlist");btn.classList.contains("active")?e.classList.remove("active"):e.classList.add("active"),btn.classList.contains("active")?btn.classList.remove("active"):btn.classList.add("active")});